% Demo1%% Simple script for testing targets & dots (dots8)%% does not require response, just shows some targets and then the dotstry    clear all    %initialize the screen    screenInfo = openExperiment(38,50,0);       % initialize targets    targets = setNumTargets(6);    % initialize dots    dotInfo = createMinDotInfo;    targets = newTargets(screenInfo, targets, [1 2 3], [0 -50 -200], [0 -50 130], [5 10 10], [0 255 255; 0 255 0; 255 0 0]);    showTargets(screenInfo, targets, [1 2 3]);    pause(0.5)        targets = newTargets(screenInfo, targets, [4 5 6], [0 0 10], [50 -50 -50], [10 10 10], [255 255 0; 0 0 255; 255 0 255]);        showTargets(screenInfo, targets, [1 2 3 4 5 6]);    pause(0.5)        disp('change color')    %targets    targets = newTargets(screenInfo, targets, [1 3 5], [], [], [], [255 255 0; 0 0 255; 255 0 255]);    showTargets(screenInfo, targets, [1 2 3 4 5 6]);    pause(0.5)        disp('change diameter')    targets = newTargets(screenInfo, targets, [2 4 6], [], [], [20 20 20], []);        showTargets(screenInfo, targets, [1 2 3 4 5 6]);    pause(0.5)        disp('change position')        targets = newTargets(screenInfo, targets, [2 4 6], [], [0 -15 15], [], []);        showTargets(screenInfo, targets, [1 2 3 4 5 6]);    pause(0.5)        showTargets(screenInfo, targets, [1 2 5]);    pause(0.5)        dotInfo;    screenInfo;        targets.show = [1 2 5];    [frames, rseed, start_time, end_time, response, response_time] = dotsX(screenInfo, dotInfo, targets);        showTargets(screenInfo, targets, [1 2 3])    pause(1)    % clear the screen and exit    closeExperiment;catch    disp('caught')    closeExperiment;end;