function targets = setNumTargets(numTargets)% targets = setNumTargets(numTargets)% creates the targets structure, default number of targets is 8 %% fields (columns) are: rects, colors, x, y, d% created by MKMK July, 2006if nargin < 1 || isempty(numTargets)	numTargets = 8;endtargets.rects = zeros(numTargets, 4);targets.colors = zeros(numTargets, 3);targets.x = zeros(numTargets, 1);targets.y = zeros(numTargets, 1);targets.d = zeros(numTargets, 1);